import React from "react";
//images
import Logo from "../assets/MIG3.png";
//motion
import { motion } from "framer-motion";

//variants
import { fadeIn } from "../variants";

export default function Login({ isOpen, children, closeLogin }) {
  if (!isOpen) return null;

  return (
    <div className="overlay">
      <motion.div
        initial={{ opacity: 0, scale: 0 }} // Set the initial animation properties
        animate={{ opacity: 1, scale: 1 }} // Set the animation properties when the component appears
        className="overlay-content usertxt mx-auto flex-col gap-y-4 min-h-[35vh] min-w-[50vh] lg:min-h-[35vh]"
      >
        <div className="header">
          <button className="btn btn-sm" onClick={closeLogin}>
            X
          </button>
        </div>
        <div>
          <img src={Logo} alt="" width={100} height={80} />
        </div>
        {children}
        <div className="input-container gap-x-4">
          USERNAME:
          <input className="input input-inside" id="user" name="user"></input>
        </div>
        <div className="input-container gap-x-4">
          PASSWORD:
          <input className="input input-inside" id="pass" name="pass"></input>
        </div>
        <button className="btn btn-sm">SUBMIT</button>
      </motion.div>
    </div>
  );
}
