import React from "react";

//images
import Image from "../assets/avatar1.jpg";

//icons
import { FaLinkedinIn, FaYoutube, FaGithub } from "react-icons/fa";

//type animation
import { TypeAnimation } from "react-type-animation";

//motion
import { motion } from "framer-motion";

//variants
import { fadeIn } from "../variants";

const Banner = () => {
  return (
    <section
      className="min-h-[75vh] lg:min-h-[78vh] flex items-center"
      id="home"
    >
      <div className="container mx-auto">
        <div className="flex flex-col gap-y-8 lg:flex-row lg:items-center lg:gap-x-12">
          {/* text */}
          <div className="flex-1 text-center font-secondary lg:text-left">
            <motion.h1
              variants={fadeIn("up", 0.3)}
              initial="hidden"
              whileInView={"show"}
              viewport={{ once: false, amount: 0.7 }}
              className="text-[55px] font-bold leading-[0.8] lg:text-[110px]"
            >
              MIGO <span>BAUTISTA</span>
            </motion.h1>
            <motion.div
              variants={fadeIn("up", 0.4)}
              initial="hidden"
              whileInView={"show"}
              viewport={{ once: false, amount: 0.7 }}
              className="mb-6 text-[36px] lg:text-[60px] font-secondary font-semibold uppercase leading-[1]"
            >
              <span className="text-white mr-4">I am a </span>
              <TypeAnimation
                sequence={[
                  "Developer EME",
                  2000,
                  "Programmer",
                  2000,
                  "MEKUS MEKUS",
                  2000,
                ]}
                speed={50}
                className="text-accent"
                wrapper="span"
                repeat={Infinity}
              ></TypeAnimation>
            </motion.div>
            <motion.p
              variants={fadeIn("up", 0.3)}
              initial="hidden"
              whileInView={"show"}
              viewport={{ once: false, amount: 0.7 }}
              className="mb-8 max-w-lg mx-auto lg:mx-0"
            >
              I don't know yet what to write here but soon for sure there will
              be something in here hahaha i am just trying things first before i
              finalize my portfolio.{" "}
              <span className="font-bold">NATUS VINCERE!</span>
            </motion.p>
            <div className="flex max-w-max gap-x-6 items-center mb-12 mx-auto lg:mx-0">
              <button className="btn btn-lg">Contact me</button>
              <a href="#" className="text-gradient btn-link">
                MY PORFOLIO
              </a>
            </div>
            {/* socials */}
            <div className="flex text-[20px] gap-x-6 max-w-max mx-auto lg:mx-0">
              <a href="# ">
                <FaYoutube />
              </a>
              <a href="# ">
                <FaLinkedinIn />
              </a>
              <a href="# ">
                <FaGithub />
              </a>
            </div>
          </div>
          {/* image */}
          <div className="round-image hidden lg:flex">
            <img src={Image} alt=""></img>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Banner;
